import Foundation

func solve1(_ input: String) -> Int {
    var sum = 0
    
    for var i in input.indices {
        var j = input.index(after: i)
        if j == input.endIndex {
            j = input.startIndex
        }
        
        if input[i] == input[j] {
            sum += Int(String(input[i]))!
        }
    }
    
    return sum
}

func rotate(_ s: String, by amount: Int) -> String {
    return String((s.suffix(amount) + s).prefix(s.count))
}

func solve2(_ input: String) -> Int {
    assert(input.count % 2 == 0)
    
    let leftInput = input
    let rightInput = rotate(input, by: input.count / 2)
    var sum = 0
    
    zip(leftInput, rightInput).forEach { (left, right) in
        if left == right {
            guard let val = Int(String(left)) else {
                print("Oops!")
                return
            }
            sum += val
        }
    }
    
    return sum
}

func day1() {
    let inputs = ["1123441", "1122", "1111", "1234", "91212129", ""]
    
    print("solve1():")
    for s in inputs {
        print("\(s): \(solve1(s))")
    }
    
    let inputs2 = ["1212", "1221", "123425", "123123", "12131415", ""]
    
    print("solve2():")
    for s in inputs2 {
        print("\(s): \(solve2(s))")
    }
}

day1()

